// Request Schema
export const BrandfetchRequestSchema = z.object({
  domain: z.string().min(1).describe('Domain to fetch brand for'),
  includeColors: z.boolean().default(true),
  includeFonts: z.boolean().default(true),
  logoFormats: z.array(z.enum(['svg', 'png'])).default(['svg', 'png']),
  logoTypes: z.array(z.enum(['logo', 'icon', 'symbol'])).default(['logo', 'icon', 'symbol']),
});

// Response Schema
export const BrandfetchResponseSchema = z.object({
  name: z.string(),
  domain: z.string(),
  logos: z.array(z.object({
    type: z.enum(['logo', 'icon', 'symbol']),
    formats: z.array(z.object({
      src: z.string().url(),
      format: z.enum(['svg', 'png', 'jpeg']),
      size: z.number().optional(),
    })),
  })),
  colors: z.array(z.object({
    hex: z.string(),
    type: z.enum(['primary', 'secondary', 'accent']),
  })).optional(),
  fonts: z.array(z.object({
    name: z.string(),
    type: z.enum(['heading', 'body']),
  })).optional(),
});

// src/app/api/v2/brandfetch/__tests__/brandfetch.api.test.ts

describe('POST /api/v2/brandfetch', () => {
  it('should fetch brand data for valid domain', async () => {
    const response = await POST({ domain: 'google.com' });
    expect(response.status).toBe(200);
    expect(response.body.logos).toBeDefined();
  });

  it('should include colors when requested', async () => {
    const response = await POST({ domain: 'google.com', includeColors: true });
    expect(response.body.colors).toBeDefined();
  });

  it('should filter logo formats', async () => {
    const response = await POST({ domain: 'google.com', logoFormats: ['svg'] });
    response.body.logos.forEach(logo => {
      logo.formats.forEach(f => expect(f.format).toBe('svg'));
    });
  });

  it('should return 400 for invalid domain', async () => {
    const response = await POST({ domain: '' });
    expect(response.status).toBe(400);
  });

  it('should handle rate limiting with retry', async () => {
    // Mock rate limit response, verify retry behavior
  });
});

// src/app/api/v2/brandfetch/route.ts

export async function POST(request: Request) {
  const body = await request.json();
  const parsed = BrandfetchRequestSchema.safeParse(body);

  if (!parsed.success) {
    return Response.json({ error: parsed.error }, { status: 400 });
  }

  const { domain, includeColors, includeFonts, logoFormats, logoTypes } = parsed.data;

  const brandData = await fetchWithRetry(
    `https://api.brandfetch.io/v2/brands/${domain}`,
    { headers: { Authorization: `Bearer ${process.env.BRANDFETCH_API_KEY}` }}
  );

  // Filter logos by format and type
  const filteredLogos = brandData.logos
    .filter(l => logoTypes.includes(l.type))
    .map(l => ({
      ...l,
      formats: l.formats.filter(f => logoFormats.includes(f.format))
    }));

  return Response.json({
    name: brandData.name,
    domain: brandData.domain,
    logos: filteredLogos,
    colors: includeColors ? brandData.colors : undefined,
    fonts: includeFonts ? brandData.fonts : undefined,
  });
}
