// Combined Schema (imports from source APIs)
export const CreateAIBlogPostSchema = z.object({
  // AI Generation inputs
  topic: z.string().min(1).describe('Blog post topic'),
  tone: z.enum(['professional', 'casual', 'technical']).default('professional'),
  wordCount: z.number().min(100).max(5000).default(1000),

  // WordPress inputs
  wpSiteUrl: z.string().url().describe('WordPress site URL'),
  status: z.enum(['publish', 'draft', 'pending']).default('draft'),
  categories: z.array(z.number()).optional(),
  tags: z.array(z.string()).optional(),

  // Orchestration options
  generateSeo: z.boolean().default(true),
  autoPublish: z.boolean().default(false),
});

export const CreateAIBlogPostResponseSchema = z.object({
  success: z.boolean(),
  content: z.object({
    title: z.string(),
    body: z.string(),
    excerpt: z.string().optional(),
    seoMeta: z.object({...}).optional(),
  }),
  wordpress: z.object({
    postId: z.number(),
    postUrl: z.string().url(),
    status: z.string(),
  }).optional(),
  errors: z.array(z.string()).optional(),
});

// Integration tests for combined endpoint (mocked sources)
describe('POST /api/v2/create-ai-blog-post', () => {
  it('generates content and saves as WordPress draft', async () => {
    const response = await POST({
      topic: 'Benefits of TypeScript',
      status: 'draft',
      wpSiteUrl: 'https://example.com',
    });
    expect(response.success).toBe(true);
    expect(response.content.title).toBeDefined();
    expect(response.wordpress.postId).toBeDefined();
  });

  it('handles AI generation failure with retry', async () => {...});
  it('saves draft when WordPress publish fails', async () => {...});
  it('generates SEO metadata when requested', async () => {...});
  it('respects caller-specified post status', async () => {...});
});

// Orchestration implementation
export async function POST(request: Request) {
  const input = CreateAIBlogPostSchema.parse(await request.json());

  // Step 1: Generate content with AI (with retry)
  const content = await retryAsync(
    () => generateBlogContent(input.topic, input.tone, input.wordCount),
    { retries: 3 }
  );

  // Step 2: Generate SEO if requested
  const seoMeta = input.generateSeo 
    ? await generateSeoMetadata(content.title, content.body)
    : undefined;

  // Step 3: Publish to WordPress (with draft fallback)
  let wordpress;
  try {
    wordpress = await publishToWordPress({
      ...content,
      seoMeta,
      status: input.status,
      siteUrl: input.wpSiteUrl,
    });
  } catch (error) {
    // Fallback: save as draft
    wordpress = await publishToWordPress({ ...content, status: 'draft' });
  }

  return Response.json({ success: true, content, wordpress });
}
