// Button.types.ts

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  /** Loading state with spinner */
  isLoading?: boolean;
  /** Render as child element (link, etc.) */
  asChild?: boolean;
}

export const buttonVariants = cva(
  'inline-flex items-center justify-center rounded-md text-sm font-medium...',
  {
    variants: {
      variant: {
        default: 'bg-primary text-primary-foreground hover:bg-primary/90',
        destructive: 'bg-destructive text-destructive-foreground...',
        outline: 'border border-input bg-background hover:bg-accent...',
        secondary: 'bg-secondary text-secondary-foreground...',
        ghost: 'hover:bg-accent hover:text-accent-foreground',
        link: 'text-primary underline-offset-4 hover:underline',
      },
      size: {
        default: 'h-10 px-4 py-2',
        sm: 'h-9 rounded-md px-3',
        lg: 'h-11 rounded-md px-8',
        icon: 'h-10 w-10',
      },
    },
    defaultVariants: {
      variant: 'default',
      size: 'default',
    },
  }
);

// Button.test.tsx

describe('Button', () => {
  describe('Rendering', () => {
    it('renders with default variant', () => {...});
    it('renders children correctly', () => {...});
  });

  describe('Variants', () => {
    it.each(['default', 'destructive', 'outline', 'secondary', 'ghost', 'link'])(
      'renders %s variant', (variant) => {...}
    );
  });

  describe('Sizes', () => {
    it.each(['default', 'sm', 'lg', 'icon'])(
      'renders %s size', (size) => {...}
    );
  });

  describe('States', () => {
    it('renders loading state with spinner', () => {...});
    it('disables button when loading', () => {...});
    it('renders disabled state', () => {...});
  });

  describe('Accessibility', () => {
    it('has correct focus styles', () => {...});
    it('shows aria-busy when loading', () => {...});
  });
});

// Button.tsx

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, isLoading, asChild = false, children, ...props }, ref) => {
    const Comp = asChild ? Slot : 'button';

    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        disabled={isLoading || props.disabled}
        aria-busy={isLoading}
        {...props}
      >
        {isLoading && (
          <svg className="mr-2 h-4 w-4 animate-spin" viewBox="0 0 24 24">
            <circle cx="12" cy="12" r="10" stroke="currentColor" />
          </svg>
        )}
        {children}
      </Comp>
    );
  }
);
Button.displayName = 'Button';

// Button.stories.tsx

export default {
  title: 'Components/Button',
  component: Button,
  argTypes: {
    variant: { control: 'select', options: ['default', 'destructive', ...] },
    size: { control: 'select', options: ['default', 'sm', 'lg', 'icon'] },
  },
} satisfies Meta<typeof Button>;

export const Default: Story = { args: { children: 'Button' } };
export const Destructive: Story = { args: { variant: 'destructive', children: 'Delete' } };
export const Outline: Story = { args: { variant: 'outline', children: 'Outline' } };
export const Secondary: Story = { args: { variant: 'secondary', children: 'Secondary' } };
export const Ghost: Story = { args: { variant: 'ghost', children: 'Ghost' } };
export const Link: Story = { args: { variant: 'link', children: 'Link' } };
export const Small: Story = { args: { size: 'sm', children: 'Small' } };
export const Large: Story = { args: { size: 'lg', children: 'Large' } };
export const Loading: Story = { args: { isLoading: true, children: 'Loading...' } };
export const Disabled: Story = { args: { disabled: true, children: 'Disabled' } };
